#include <iostream>
using namespace std;

int fibonacci(int n) {
   if(n == 0 || n == 1) {
      return n;
   }
   else {
      return fibonacci(n-1) + fibonacci(n-2);
   }
}

int main() {
   int n;
   cout << "Enter the value of n: ";
   cin >> n;
   cout << "The " << n << "th Fibonacci number is: " << fibonacci(n) << endl;
   return 0;
}

#include <iostream>
using namespace std;

int sequence(int n) {
   if(n == 1) {
      return 0;
   }
   else if(n == 2 || n == 3) {
      return -1;
   }
   else {
      return sequence(n-1) + sequence(n-2) + sequence(n-3);
   }
}

int main() {
   int n;
   cout << "Enter the value of n: ";
   cin >> n;
   cout << "The " << n << "th number in the sequence is: " << sequence(n) << endl;
   return 0;
}

