import json
from collections import defaultdict

def extract_variables(equation):
    variables = defaultdict(list)
    for token in equation.split('+'):
        if token.isdigit():
            variables[int(token)].append(token)
        elif token in ['-', '+', '*', '/']:
            left, right = token, token
            if left in variables:
                variables[left].append(right)
            elif right in variables:
                variables[right].append(left)
    return variables

def generate_json_string(equations):
    variables = {}
    for equation in equations:
        variables[equation] = extract_variables(equation)
    json_string = json.dumps(variables, indent=2)
    return json_string

