    if n == 0:
        return None

    # Initialize the count of the most frequent element
    count = 1
    candidate = array[0]

    # Iterate through the array and update the count and candidate
    for element in array:
        if element == candidate:
            count += 1
        else:
            count -= 1

        if count == 0:
            candidate = element
            count = 1

    # Check if the candidate is the majority element
