function generateOrinal offspringDistribtion() {
  var blue = parseInt(Math.random() * 4);
  var green = parseInt(Math.random() * 4);
  var turquoise = parseInt(Math.random() * 4);

  var parents = [blue, green, turquoise]; // Random selection

  var offspringProbabilities = [
    blue * green * turquoise / (blue + green + turquoise),
    green * blue * turquoise / (green + blue + turquoise),
    blue * green * turquoise / (blue + green + turquoise)
  ];

  var offspringFreqencies = [
    blue / parents.length,
    green / parents.length,
    turquoise / parents.length
  ];

  // Repeat until desired outcome
  for (var i = 0; i < 1000; i++) {
    var parents = parents.sort((a, b) => b - a);
    var offspringProbabilities = offspringProbabilities.sort((a, b) => b - a);
    var offspringFreqencies = offspringFreqencies.sort((a, b) => b - a);

    var offspring = [];
    for (var j = 0; j < parents.length; j++) {
      var parent = parents[j];
      var offspringProbabilitiesForThisColor = offspringProbabilities[j] * probabilityOfEachOption(parent);
      var offspringFreqenciesForThisColor = offspringFreqencies[j] * frequencyOfEachOption(parent);

      var randomNumber = parseInt(Math.random() * offspringFreqenciesForThisColor[0]);
      offspring.push(randomNumber);
    }

    offspringFreqencies = offspringFreqencies.filter((f, i) => i !== randomNumber); // Keep only non-current offspring

    // Randomly pick a current offspring and replace parents
    var currentOffspring = offspring.sort((o1, o2) => o2 - o1)[0];
    parents.splice(randomNumber, 1);
    parents.splice(currentOffspring, 0, offspring[currentOffspring]);
  }

  return offspringFreqencies.map((f, i) => ({ parents: parents, offspring: offspringFreqencies[i] * f }));
}

function probabilityOfEachOption(parent) {
  return (parent.blue + parent.green + parent.turquoise) / 3;
}

function frequencyOfEachOption(parent) {
  return (parent.blue + parent.green + parent.turquoise) / 3;
}
