class TranslateAssistant:
    def translate(self, word):
        raise NotImplementedError('Please implement')

    def rhyme(self, w1, w2):
        return w1.translate().lower() == w2.translate().lower()

    def process_pair(self, pair):
        rhymes = self.rhyme(pair[0], pair[1])
        if rhymes:
            return ''.join([pair[0] + ' ' + pair[1], rhymes])
        else:
            return 'NONE'

def main():
    while True:
        try:
            word1 = input('Word1: ')
            word2 = input('Word2: ')
            assistant = TranslateAssistant()
            output = assistant.process_pair([word1, word2])
            if output == 'NONE':
                print(f'{word1} does not rhyme with {word2} in Hebrew')
            else:
                print(output)
        except:
            break

if __name__ == '__main__':
    main()
