class Queue:
    def __init__(self):
        self.stack1 = [] 
        self.stack2 = [] 

    def is_empty(self):
        return not (self.stack1 or self.stack2)

    def enqueue(self, val):
        if not self.is_empty():
	    raise Exception('First enqueue failed')
        self.stack1.append(val)

    def dequeue(self): 
        if self.is_empty(): 
	    raise Exception('First dequeue failed')
        return self.stack1.pop() 

    def peek(self): 
        if self.is_empty(): 
	    raise Exception('Peek failed')
        return self.stack2.pop()

