import numpy as np

def find_longest_subsequence(str1, str2):
    """Returns the length of longest common subsequence of two strings"""
    if len(str1) < len(str2):
        return find_longest_subsequence(str2, str1)

    seq1 = np.array(str1, dtype=float)
    seq2 = np.array(str2, dtype=float)
    n = len(str1)
    lps = [0] * n
    for i in range(n):
        for j in range(i+1, n):
            if seq1[i] == seq2[j]:
                lps[i] = max(lps[i], lps[j] + 1)
    return lps[n-1]

