        if self.stack2:
            self.stack1, self.stack2 = self.stack2, self.stack1
        while not self.stack1:
            self.stack2.append(self.stack1.pop())
        self.stack2.pop()

    def peek(self):
        if self.stack2:
            self.stack1, self.stack2 = self.stack2, self.stack1
        return self.stack1[len(self.stack1)-1]

