#include <iostream>
#include <fstream>
#include <string>

void countWords(const std::string& filename, const std::string& word) {
    std::ifstream stream(filename);
    if (!stream) {
        std::cerr << "Error reading file: " << filename << std::endl;
        return;
    }

    std::string line;
    int count = 0;
    while (getline(stream, line)) {
        std::stringstream ss(line);
        ss >> word;
        if (word == word) {
            ++count;
        }
    }

    std::cout << "Number of occurrences of " << word << " in " << filename << " is: " << count << std::endl;
}

int main() {
